import React from "react";

function TinderCards() {
  const [people, setPeople] = useState([
    {
      name: "steve jobs",
      url: "https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cg_face%2Cq_auto:good%2Cw_300/MTY2MzU3OTcxMTUwODQxNTM1/steve-jobs--david-paul-morrisbloomberg-via-getty-images.jpg",
    },
    {
      name: "mark zuckerberg",
      url: "https://api.time.com/wp-content/uploads/2020/07/Mark-Zuckerberg-Surfing.jpg?w=800&quality=85",
    },
  ]);

  //   BAD
  //      const people = []
  //     people.push('farzad', 'phil')

  //   GOOD (Push to an array in REACT)
  //   setPeople([...people, 'farzad', 'phil'])

  return (
    <div>
      <h1> Tinder Cards</h1>

      {people.map((person) => (
        <TinderCard>
          <div 
          style={{ backgroundImage: `url(${person.url})` }}
          className="card">
              <h3>{person.name}</h3>
          </div>
        </TinderCard>
      ))}
    </div>
  );
}

export default TinderCards;
